import React, { useEffect, useState, Fragment } from "react";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { styled } from '@mui/material/styles';
import PopOver from "./Popover";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

class ClassListCompanies extends React.Component {

  state = {
    books: [],
    open: false,
    company: "",
    open: false
  }

  fetchBooks = () => {
    fetch('http://localhost:5000/by_company')
      .then((response) => response.json())
      .then(booksList => {
        this.setState({ books: booksList });
      });

  }  

  handleClickOpen = (e) => {
    this.state.company 
    setOpen(true);
    getTitles(e.target.id);
    console.log(title);
  };

  handleClose = () => {
    setOpen(false);
  };  

  render() {
    console.log(this.state.books)
    return (
      <Fragment>
        <Box style={{ width: 700, margin: "auto" }} className="mt-5">
          <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 16 }}>
            {this.state.books.map(value => (
              <Grid item xs={2} sm={4} md={4} key={value}>
                <Item
                  type="button"
                  id={value}
                  onClick={this.handleClickOpen}
                >{value}
                </Item>
              </Grid>
            ))}
          </Grid>
        </Box>
        {/* {title['positions_list'].map(value =>(
              <h1>
                {value}
              </h1>
      ))}
             */}
        <PopOver open={open} company={company} handleClose={handleClose} title={title} />

      </Fragment>
    )
  }
}

export default ClassListCompanies;
