import React, { useEffect, useState, Fragment } from "react";
import 'bootstrap/dist/css/bootstrap.css';

export default function CardCompany( {dicCompany, company} ) {

  const [position, setPosition] = useState("")

  const handleClickRadio = (e) => {
    setPosition(e.target.value);
  };

  return (
    <div className="card" style={{ width: 288}}>
      <div className="card-body">
        <h5 className="card-title text-center">{dicCompany.company_name ? company : "Select company"}</h5>
        <h6 className="card-subtitle mb-2 text-muted text-center">#{position ? position : "Select position"}#</h6>
        <p className="card-text">
          {dicCompany !== null ?
            dicCompany.positions_list.map(value => (
            <div class="form-check">  
              <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value={value} onChange={handleClickRadio}/>
              <label class="form-check-label" for="gridRadios2">
                {value}
              </label>
            </div>
            )) : <p>
              No data
            </p>}
        </p>
      </div>
    </div>
  );
}
