import React, { useEffect, useState, Fragment } from "react";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Link from '@mui/material/Link';


const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': {
    padding: theme.spacing(2),
  },
  '& .MuiDialogActions-root': {
    padding: theme.spacing(1),
  },
}));

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props;

  return (
    <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
};

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

BootstrapDialogTitle.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func.isRequired,
};


const ListCompanies= () => {

  const setValue = (event) => setCompany(event.target.id)
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [company, setCompany] = useState("");
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const ClickPopover = () => {
    useEffect(() => {
      fetch("http://localhost:5000/by_company/")
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw response;
        })
        .then(data => {
          setData(data);
        })
        .catch((error) => {
          console.error("Error fetching data: ", error);
          setError(error);
        })
        .finally(() => {
          setLoading(false);
        });
    }, []);
    if (loading) return "Loading...";
    if (error) return "Error!";
  };

  const preventDefault = (event) => event.preventDefault();

  useEffect(() => {
    fetch("http://localhost:5000/by_company")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw response;
      })
      .then(data => {
        setData(data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);
  if (loading) return "Loading...";
  if (error) return "Error!";

  console.log(company);

  return (
    <Fragment>
      <Box style={{ width: 700, margin: "auto" }} className="mt-5">
        <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 16 }}>
          {data.map(value => (
            <Grid item xs={2} sm={4} md={4} key={value}>
              <Item
                type="button"
                onClick={(e) => { handleClickOpen(e); setValue(e); ClickPopover(e)}}
                id={value}
                >{value}
              </Item>
            </Grid>
          ))}
        </Grid>
      </Box>

      <div>
        <Button variant="outlined" onClick={handleClickOpen}>
          Open dialog
        </Button>
        <BootstrapDialog
          onClose={handleClose}
          aria-labelledby="customized-dialog-title"
          open={open}
        >
          <BootstrapDialogTitle className="text-center" onClose={handleClose}>
            {company}
          </BootstrapDialogTitle>
          <DialogContent dividers>
            <Box
              sx={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                typography: 'body1',
                '& > :not(style) + :not(style)': {
                  ml: 2,
                },
              }}
              onClick={preventDefault}
            >
              <Link href="#" underline="hover">
                Software Engineer +
              </Link>
              <Link href="#" underline="hover">
                Machine Learning +
              </Link>
              <Link href="#" underline="hover">
                Full Stack +
              </Link>
              <Link href="#" underline="hover">
                API +
              </Link>
              <Link href="#" underline="hover">
                Data +
              </Link>
              <Link href="#" underline="hover">
                DevOps +
              </Link>

            </Box>

          </DialogContent>
          <DialogContent>

          </DialogContent>
        </BootstrapDialog>
      </div>

    </Fragment>
  );
};

export default ListCompanies;
