import React, { useEffect, useState, Fragment } from "react";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { styled } from '@mui/material/styles';
import PopOver from "./Popover";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const ListCompanies = () => {

  const [companies, setCompanies] = useState(null);
  const [title, setTitle] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [company, setCompany] = useState("");
  const [open, setOpen] = useState(false);
  const setValue = (event) => setCompany(event.target.id)

  const handleClickOpen = async (e) => {

    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    fetch("http://localhost:5000/by_company")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw response;
      })
      .then(data => {
        setData(data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  const getTitles = async (comp) => {
    try {
      const response = await fetch("http://localhost:5000/by_company/" + comp);
      const jsonData = await response.json();
      setTitle(jsonData);
      return jsonData;
    } catch (err) {
      console.error(err.message);
    }
  };

  if (loading) return "Loading...";
  if (error) return "Error!";

  return (
    <Fragment>
      <Box style={{ width: 650, margin: "auto" }} className="mt-5">
        <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 16 }}>
          {data.slice(0, 1).map(value => (
            <Grid item xs={2} sm={4} md={4} key={value}>
              <Item
                type="button"
                id={value}
              >{value}
              </Item>
            </Grid>
          ))}
          <Grid item xs={2} sm={4} md={4} key="more">
            <Item
              type="button"
              id="more"
              onClick={handleClickOpen}
            >MORE +
            </Item>
          </Grid>
        </Grid>
      </Box>


      <PopOver open={open} handleClose={handleClose} companies={companies} />

    </Fragment>
  );
};

export default ListCompanies;
